custom.scss:
.hlfilter{
	#sideAllCategories,
	.sidebarBlock.Shop-by-price,
	#sideShopByBrands{
		display: none;
	}
	.pagination-item.pagination-item--previous > a,
	.pagination-item.pagination-item--next > a{
		line-height: 1;
		margin-top: 3px;
	}
}
#halofilter{
	.side-module-heading{
	    font-family: Arimo,Arial,Helvetica,sans-serif;
	    font-size: 14px;
	    font-weight: 700;
	    line-height: 1.5;
    	color: #000;
	    text-transform: capitalize!important;
	    padding: 9px 15px!important;
		margin: 0 0 10px;
		border-bottom: 1px solid #ddd;
	    .expand_block{
	    	top: 50%!important;
	    	right: 9px!important;
	    	transform: translateY(-50%);
	    }
	}
	.BlockContent{
		padding: 0 0 0 15px;
		ul{
			li{
				display: flex!important;
				align-items: center;
				width: 100%;
				margin: 0 0 .35714rem 0!important;
				input[type=checkbox]{
					&:after{
						border-color: #ccc;
						height: 1.14286rem;
						width: 1.14286rem;
						border-radius: 0;
					}
					&:checked{
						&:after{
							background-color: #666666;
							color: #fff!important;
							border-color: #666666;
						}
						+ label{
							color: #666666;
							span + span{
								color: #666666;
							}
						}
					}
				}
				label{
					font-family: "Arimo", Arial, Helvetica, sans-serif;
					font-size: 12px;
				    font-weight: 400;
				    margin: 0;
			        color: #585858!important;
				    position: relative;
				    vertical-align: baseline;
				    width: 100%;
				    cursor: pointer;
				}
				&:hover{
					label{
						color: #666666!important;
						span + span{
							color: #666666;
						}
					}
				}
				&:first-child{
					margin-top: .35714rem!important;
				}
			}
			&::-webkit-scrollbar{
			    width: 6px;
			}
			/* Track */
			&::-webkit-scrollbar-track{
			    background: #f1f1f1; 
			}
			/* Handle */
			&::-webkit-scrollbar-thumb{
			    background: #a9a9a9; 
			}
			/* Handle on hover */
			&::-webkit-scrollbar-thumb:hover{
			    background: #a9a9a9;
			}

		}
		.hf_clear{
			margin: 0;
			a{
				padding: 0;
			    font-size: 12px;
			    font-weight: 400;
			    vertical-align: top;
			    display: inline-block;
			    text-transform: capitalize;
			    color: #585858;
			    letter-spacing: 0;
			    font-style: normal;
				&:hover{
					color: #757575;
				}
			}
		}
	}
	#SidebarFilters{
		border: 1px solid #c8c8c8!important;
		padding-bottom: 0!important;
		.HaloBlockContent{
			.alert{
				background-color: transparent;
				color: #585858;
				border: 0;
				margin: 0;
			}
			.list-filter{
				padding: 0;
				margin: 0;
				border: 0;
				li{
					margin:0 0 0 .78571rem;
					padding: 0;
					a{
						font-family: Arimo,Arial,Helvetica,sans-serif;
						font-size: 12px;
						font-weight: 400;
						line-height: 30px;
					    background: #e5e5e5;
					    color: #585858;
					    border-radius: 4px;
					    padding: 0 10px!important;
					    text-decoration: none;
					    transition: none;
					    margin: 0 0 10px;
					    &:after{
					    	color: #989898;
					    	font-size: 10px;
					    	vertical-align: 2px;
					    }
					    &:hover{
					    	color: #757575;
					    	&:after{
					    		color: #757575;
					    	}
					    }
					}
				}
			}
		}
		.filter-clear-all{
			top: 7px;
			right: 15px;
			font-family: Arimo,Arial,Helvetica,sans-serif;
			font-size: 12px;
			font-weight: 400;
			color: #333;
			text-transform: unset;
			&:hover{
				color: #757575;
			}
		}
	}
	#halofilter_form{
		margin-top: 2.03846rem;
		.side-module-heading{
			margin-bottom: 0;
		}
		#price{
			padding: 15px 15px 15px 0;
		}
		#max_price,
		#min_price{
			border-color: #282828;
			color: #666;
			border-radius: 0;
			font-weight: 400;
			&:focus{
				outline: 0;
				border-color: #989898;
			}
		}
		#price_apply{
			border-color: transparent;
			background: linear-gradient(to bottom, #002868 0, #1b5bb2 100%);
			color: #fff;
			border-radius: 3px;
			font-weight: 700;
			transition: all 0.3s ease;
    		font-family: "Arimo", Arial, Helvetica, sans-serif;
			letter-spacing: 0;
			height: 38px;
			line-height: 38px;
			padding: 0px 21px;
			&:hover{
			    background: linear-gradient(to bottom, #3f8ece 0, #1b5bb2 100%);
			    border-color: transparent;
			}
		}
	}
}
.hlfilter{
	.module-wrapper{
		.prod-price{
			> p{
				display: none;
			}
		}
		&.login-for-pricing{
			.prod-price{
				.price-section{
					display: none;
				}
				> p{
					display: block;
				}
			}
			.buttons-wrapper{
				> .btn{
					display: none;
				}
			}
		}
	}
	#homeNewProducts{
		.prod-price{
			> p{
				display: block;
			}
		}
	}
	.actionBar{
		min-width: unset;
	    text-align: right;
	    padding: 0;
	    margin: 0;
	    float: unset;
	    fieldset{
	    	float: none;
	    	.form-field{
	    		margin: 0;
	    	}
	    }
	    label{
	    	display: inline-block;
		    font-size: 11px;
		    letter-spacing: .05em;
		    position: relative;
		    top: 0;
		    margin-right: 11px;
		    font-family: Poppins,Arial,Helvetica,sans-serif;
    		font-weight: 700;
    		text-transform: uppercase;
	    }
	    select{
	    	display: inline-block;
		    max-width: 180px;
		    background-color: #fff;
	    }
	    @media (max-width: 480px){
		    float: none;
		    text-align: left;
		    width: 100%;
		    clear: both;
		    padding-top: 10px;
	    }
	}
}
.mCustomScrollbar{
	-ms-touch-action: pinch-zoom;
    touch-action: pinch-zoom;
    .mCustomScrollBox{
    	position: relative;
	    overflow: hidden;
	    height: 100%;
	    max-width: 100%;
	    outline: 0;
	    direction: ltr;
    }
}
.mCSB_container.mCS_no_scrollbar_y.mCS_y_hidden, .mCSB_inside>.mCSB_container{
	margin-right: 0;
}
.mCSB_container{
    overflow: hidden;
    width: auto;
    height: auto;
}
.mCSB_scrollTools,
.mCSB_scrollTools .mCSB_buttonDown,
.mCSB_scrollTools .mCSB_buttonLeft,
.mCSB_scrollTools .mCSB_buttonRight,
.mCSB_scrollTools .mCSB_buttonUp,
.mCSB_scrollTools .mCSB_dragger .mCSB_dragger_bar{
	transition: opacity .2s ease-in-out,background-color .2s ease-in-out;
}
.mCSB_scrollTools{
	position: absolute;
    width: 8px;
    height: auto;
    left: auto;
    top: 0;
    right: 0;
    bottom: 0;
}
.mCSB_scrollTools .mCSB_draggerContainer{
	position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    height: auto;
}
.mCSB_scrollTools .mCSB_dragger{
	cursor: pointer;
    width: 100%;
    height: 30px;
    z-index: 1;
}
.mCSB_scrollTools .mCSB_dragger .mCSB_dragger_bar{
	position: relative;
    width: 8px;
    height: 100%;
    margin: 0 auto;
    text-align: center;
    background-color: #2d2d2d;
}
.mCSB_scrollTools .mCSB_draggerRail{
	width: 8px;
    height: 100%;
    margin: 0 auto;
    background-color: #f5f5f5;
}
#quickSearch .quickSearchResults .alert{
	margin: 0;
}
#quickSearch .quickSearchResults .productItems.pop{
	margin: 0;
}
#quickSearch .quickSearchResults .productItems.pop > li.product svg{
	width: 14px!important;
    height: 14px!important;
    color: grey!important;
}
#quickSearch .quickSearchResults .productItems.pop > li a{
	font-size: 12px;
	color: #666666;
	text-transform: capitalize;
}
#quickSearch .quickSearchResults .productItems>li{
	width: 100%;
    list-style: none;
    float: none;
    clear: both;
    overflow: hidden;
}
#quickSearch .quickSearchResults .productItems.products > li article{
	margin: 0;
    padding: 0;
    border: 0; 
}
#quickSearch .quickSearchResults .productItems>li article figure{
	float: left;
    width: 20%;
    text-align: center;
    border-radius: 3px;
    margin: 0;
    background-color: #fff;
}
#quickSearch .quickSearchResults .productItems>li article figure a img {
	max-height: 70px;
}
#quickSearch .quickSearchResults .productItems>li article figcaption{
	float: left;
    margin: 0;
    text-align: left;
    width: 80%;
    padding-left: 15px;
}
#quickSearch .quickSearchResults .productItems.products > li article figcaption h4 > span{
	color: #ffffff!important;
    border: 1px solid #3f8ece!important;
    background: linear-gradient(to bottom, #3f8ece 0, #1b5bb2 100%)!important;
}
#quickSearch .quickSearchResults .productItems > li article figcaption p.sku{
	font-family: Arimo,Arial,Helvetica,sans-serif!important;
	color: #787878!important;
    line-height: 14px!important;
    display: block!important;
    margin-top: 0!important;
    margin-bottom: 4px!important;
    font-weight: 400!important;
    font-size: 12px!important;
}
#quickSearch .quickSearchResults .productItems > li article figcaption .brand{
	font-family: "Arimo", Arial, Helvetica, sans-serif!important;
	display: block!important;
    color: #787878!important;
    line-height: 1!important;
    font-size: 12px!important;
    font-weight: 700!important;
    margin-bottom: 7px!important;
    text-transform: unset!important;
    letter-spacing: 0!important;
}
#quickSearch .quickSearchResults .productItems.products li a{
	font-family: "Arimo", Arial, Helvetica, sans-serif!important;
	font-weight: 400!important;
	font-size: 12px!important;
}
#quickSearch .quickSearchResults .productItems.products li:hover .item-name a{
	text-decoration: none!important;
	color: #3f8ece!important;
}

#quickSearch .quickSearchResults .productItems > li article figcaption .item-price{
	font-family: Arimo,Arial,Helvetica,sans-serif!important;
	color: #b4b4b4!important;
}
#quickSearch .quickSearchResults .productItems > li article figcaption .item-price .SalePrice{
	color: #e95144;
	font-weight: 700;
}
#quickSearch .quickSearchResults .productItems > li article figcaption .saleoff{
	font-family: Arimo,Arial,Helvetica,sans-serif!important;
	background: #e95144!important;
	font-size: 12px!important;
}
.page-type-search .panel-body{
	background:#fff
}
.page-type-search .search-suggestion{
	font-size: 12px;
	font-weight: 400;
	letter-spacing: 0.24px;
}
.page-type-search .search-suggestion > .search-refine{
	color: #3f8ece;
	border-bottom:1px solid transparent;
}
.page-type-search .search-suggestion > .search-refine:hover{
	border-bottom:1px solid transparent;
}
.page-type-search .your_search_for {
	border: 1px solid #f8ebcf;
	padding: 12px 30px;
	background: #fbf8e3;
	border-radius: 3px;
	margin-bottom:25px;
}
.page-type-search .your_search_for p{
	color: #866e42;
	font-size: 12px;
	font-weight: 400;
}
.page-type-search .did_you_mean{
	margin-bottom: 17px;
}
.page-type-search .suggestion-trendingnow{
	color: #343434;
	font-size: 12px;
	font-weight: 700;
	letter-spacing: 0.24px;
	border-bottom: 1px solid #e5e5e5;
	margin-bottom: 18px;
	padding-bottom: 0;
}
.page-type-search .suggestion-trendingnow a{
    color: #343434;
    font-size: 12px;
    font-weight: 400;
    line-height: 28px;
    padding: 3px 15px;
    border: 1px solid #e6e6e6;
    -webkit-border-radius: 40px;
    -moz-border-radius: 40px;
    border-radius: 40px;
    margin-left: 8px;
    display: inline-block;
    line-height: 20px;
    margin-bottom: 8px;
}
.page-type-search .suggestion-trendingnow a:hover svg{
	fill: #fff;
}
.page-type-search .suggestion-trendingnow a:hover{
	color: #fff;
	background: linear-gradient(to bottom, #3f8ece 0, #1b5bb2 100%);
	border-color: #3f8ece;
}
.page-type-search .suggestion-trendingnow a svg{
    width: 14px;
    height: 14px;
    line-height: 20px;
    position: relative;
	fill: gray;
}
.page-type-search .search-suggestion h5.suggestion-title{
	font-size: 14px;
	font-weight: 700;
	text-transform: capitalize !important;
    font-family: "Arimo", Arial, Helvetica, sans-serif;
}
.page-type-search .suggestion-line ul li{
  color: #343434;
  font-size: 12px;
  font-weight: 400;
  line-height: 28px;
}
.page-type-search .suggestion-trendingnow a svg{
	margin-right:3px
}
.page-type-search .suggestion-line{
	margin-bottom:35px
}
.page-type-search #homeNewProducts.module-wrapper > .container{
	border:0;
}
.page-type-search #homeNewProducts.module-wrapper .module-heading{
	font-size: 14px;
    line-height: 1.5;
    font-weight: 700 !important;
    padding: 9px 15px !important;
    margin: 0;
    text-transform: capitalize !important;
    font-family: "Arimo", Arial, Helvetica, sans-serif;
    color: black;
    position: relative;
    box-shadow: none;
    border-radius: 0;
    margin-bottom: 10px;
    text-align: center;
}
@media (max-width: 1024px){
	#search_category{
		display: none;
	}
	.box-search{
		svg{
			display: none;
		}
	}
	.text-bottom-search{
		display: none;
	}
	#quickSearch .quickSearchResults{
		width: 100%!important;
	}
}
